# WAP to print the pyramid.

for i in range(1,4):
    for j in range(1,6):
        if(j<=3-i or j>=3+i):
            print(" ",end=" ")
        else:
            print("*",end=" ")
    print()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         